INSERT INTO `wp_posts` VALUES (6,1,'2023-02-26 09:43:08','2023-02-26 09:43:08','<!-- wp:cover {\"url\":\"http://potentials.local/wp-content/uploads/2023/04/image_2023-04-16_200349424.png\",\"id\":201,\"dimRatio\":0,\"minHeight\":700,\"align\":\"full\"} -->\n<div class=\"wp-block-cover alignfull\" style=\"min-height:700px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim-0 has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-201\" alt=\"\" src=\"http://potentials.local/wp-content/uploads/2023/04/image_2023-04-16_200349424.png\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":1,\"textColor\":\"nv-site-bg\"} -->\n<h1 class=\"has-text-align-center has-nv-site-bg-color has-text-color\">Сайт за млади програмисти</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"17px\"}},\"textColor\":\"nv-site-bg\"} -->\n<p class=\"has-text-align-center has-nv-site-bg-color has-text-color\" style=\"font-size:17px\">Чрез този сайт имате достъп до различни видове обучения, стажове и приближаващи събития</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons {\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"className\":\"is-style-primary\"} -->\n<div class=\"wp-block-button is-style-primary\"><a class=\"wp-block-button__link wp-element-button\" href=\"http://potentials.local/blog/\">Вижте още</a></div>\n<!-- /wp:button -->\n\n<!-- wp:button {\"className\":\"is-style-default\"} -->\n<div class=\"wp-block-button is-style-default\"><a class=\"wp-block-button__link wp-element-button\" href=\"http://potentials.local/контакти/\">Свържете се с нас</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"width\":48,\"height\":48,\"className\":\"icon-style is-style-default\"} -->\n<figure class=\"wp-block-image aligncenter is-resized icon-style is-style-default\"><img src=\"http://potentials.local/wp-content/themes/neve/assets/img/starter-content/icon-03.svg\" alt=\"\" width=\"48\" height=\"48\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Помощи за потенциални младежи</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Множество събития, обучения и стажанстки програми.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"width\":48,\"height\":48,\"className\":\"icon-style is-style-default\"} -->\n<figure class=\"wp-block-image aligncenter is-resized icon-style is-style-default\"><img src=\"http://potentials.local/wp-content/themes/neve/assets/img/starter-content/icon-02.svg\" alt=\"\" width=\"48\" height=\"48\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Качество</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Осигуряваме ви, че предлагаме най-добрите стажантски прогрми и обучения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"width\":48,\"height\":48,\"className\":\"icon-style is-style-rounded\"} -->\n<figure class=\"wp-block-image aligncenter is-resized icon-style is-style-rounded\"><img src=\"http://potentials.local/wp-content/themes/neve/assets/img/starter-content/icon-01.svg\" alt=\"\" width=\"48\" height=\"48\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Бързо и лестно</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Записването на дадена програма или обучение става бързо и лестно.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:cover {\"overlayColor\":\"nv-light-bg\",\"minHeight\":600,\"align\":\"full\"} -->\n<div class=\"wp-block-cover alignfull\" style=\"min-height:600px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background ha